// import ReactDOM from 'react-dom/client';
import './Footer.css';

// import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ThemeProvider from 'react-bootstrap/ThemeProvider'


function Footer() {
    // const a = { padding: '3px' };

    const classRemove = document.getElementById('footer');
    console.log(classRemove);

    const backgroundColour = {
        background: { "background": "#16161A" },
        TextColour: { "color": "#FFF" }
    }
    return (
        <footer style={backgroundColour.background} className='container-fluid'>


            <Row className='row  footerRows justify-content-md-center' id='footerRows' >
                <ThemeProvider
                    breakpoints={['xxxl', 'xxl', 'xl', 'lg', 'md', 'sm', 'xs', 'xxs']}
                    maxBreakpoint="md"
                >


                    <Col className='colDesign' md="4" >
                        <h4>CryptoVault</h4>
                        <p className='greyText'>Your one-stop destination for everything related to cryptocurrencies.</p>
                    </Col>
                    <Col className='colDesign' md="2" >
                        <h5>Explore</h5>
                        <div className="p-0 mb-1" > <a href="#"> <i class="bi bi-amd"></i>Home</a></div>
                        <div className="p-0 mb-1"> <a href="#">Dashboard</a></div>
                        <div className="p-0 mb-1"><a href="#">about</a></div>
                    </Col>
                    <Col className='colDesign' md="3">
                        <h5>Business</h5>
                        <p className="font-weight-normal p-0 mb-1">support@cryptovault.com</p>
                        <p className="font-weight-normal p-0 mb-1">+449999999999</p>
                    </Col>
                    <Col className='colDesign' md='2'>
                        <h5>Follow us</h5>
                        <p className="font-weight-normal p-0 mb-1"><a href="#">Facebook</a></p>
                        <p className="font-weight-normal p-0 mb-1"><a href="#">Instagram</a></p>
                        <p className="font-weight-normal p-0 mb-1"><a href="#">Twitter</a></p>
                        <p className="font-weight-normal p-0 mb-1"><a href="#">LinkedIn</a></p>

                    </Col>
                </ThemeProvider>
            </Row>

            <Row className='copy-right'>
                <p className='text-justify'>Â© 2023 CryptoVault. All rights reserved.</p>
            </Row>


        </ footer >

    )
}

export default Footer;
